file_name=sf_${scale}_${partition_num}_${id}

echo "Start downloading ldbc graph partition scale=${scale} partition_num=${partition_num} id=${id}..."

if curl -o ldbc_sf${scale}_bin_p${partition_num}_partition${id}.tar -L "${!file_name}"
then
    echo "Finish downloading!"
    rm -rf ./Graphs/scale_${scale}/bin_p${partition_num}/partition_${id}
    mkdir -p ./Graphs/scale_${scale}/bin_p${partition_num}/partition_${id}
    echo "Start installing ldbc graph partition scale=${scale} partition_num=${partition_num} id=${id}..."
    tar -xf ldbc_sf${scale}_bin_p${partition_num}_partition${id}.tar --directory ./Graphs/scale_${scale}/bin_p${partition_num}/partition_${id}
    rm -f ldbc_sf${scale}_bin_p${partition_num}_partition${id}.tar
    echo "Finish installing!"
fi